import "./styles.scss";
import("https://cdn.socket.io/socket.io-1.2.0.js");

const toggleDone = id => {
    const history = JSON.parse(localStorage.getItem("history"));
    const newHistory = history.map(e => e.date === id ? {...e,done:!e.done} : e);
    localStorage.setItem("history",JSON.stringify(newHistory));
  }

  const removeMessage = id => {
    const history = JSON.parse(localStorage.getItem("history"));
    const newHistory = history.map(e => e.date === id ? {...e,removed:true} : e);
    localStorage.setItem("history",JSON.stringify(newHistory));
  }

  const createMessage = msg => {
    const date = new Date(msg.date);

    const $d = $("<small>").text(
      date
        .getHours()
        .toString()
        .padStart(2, "0") +
        " : " +
        date
          .getMinutes()
          .toString()
          .padStart(2, "0")
    );
    const $t = $("<p>").text(msg.text);
    const $m = $('<li class="rounded">')
      .html($d.add($t))
      .click(function() {
        $(this).toggleClass("done");
        toggleDone(msg.date);
      });
    if (msg.done) {
      $m.addClass("done");
    }
    if (msg.removed) {
      $m.addClass("removed");
    }
    return $m;
  };

  $(function() {
    var socket = io();
    $("form").submit(function() {
      socket.emit("chat message", $("#m").val());
      $("#m").val("");
      return false;
    });

    socket.on("chat message", function(msg) {
      $("#messages").append(createMessage(JSON.parse(msg)));

      const history = JSON.parse(localStorage.getItem("history"));          
      localStorage.setItem("history",JSON.stringify([...history,JSON.parse(msg)]));

      window.scrollTo(0, document.body.scrollHeight);
    });

    socket.on("history", function(msg) {
      const serverHistory = JSON.parse(msg);
      const localHistory = JSON.parse(localStorage.getItem("history"));
      let messages = serverHistory;

      if (localHistory && localHistory.length > 0) {
        const lastMessage = localHistory[localHistory.length - 1];
        const update = serverHistory.filter(m => m.date > lastMessage.date);

        messages = localHistory.concat(update);
      } 

      localStorage.setItem("history", JSON.stringify(messages));            

      messages.forEach(message => {
        $("#messages").append(createMessage(message));
      });



      window.scrollTo(0, document.body.scrollHeight);
    });
  });
